# coding:utf-8
import json


class utils:

    @staticmethod
    def write_study(params: json) -> None:

        with open("D:/hina bot/hinabot/.venv/Lib/site-packages/nonebot/plugins/mazui_study/resource/mazui_state.json",'w',encoding='utf-8') as f:

            json.dump(params,f,indent=4)


    @staticmethod
    def read_study(state: str) -> bool:

        with open("D:/hina bot/hinabot/.venv/Lib/site-packages/nonebot/plugins/mazui_study/resource/mazui_state.json",'r',encoding='utf-8') as f:
            now_state: json = json.load(f)
            state: bool = bool(now_state[state])

        return state

    @staticmethod
    def mod_json_data(state: str,value: bool) -> json:

        with open("D:/hina bot/hinabot/.venv/Lib/site-packages/nonebot/plugins/mazui_study/resource/mazui_state.json",'r',encoding='utf-8') as f:

            params = json.load(f)

            params[state] = value

            return params
        
    @staticmethod
    def mod_json_data_dict(get_dict: dict) -> json:
        
        with open("D:/hina bot/hinabot/.venv/Lib/site-packages/nonebot/plugins/mazui_study/resource/mazui_state.json",'r',encoding='utf-8') as f:
            
            params = json.load(f)
            
            for key in get_dict:
                value = get_dict[key]
                params[key] = value
                
            return params